import { useState, useEffect } from 'react';
import apiClient from '../services/api';
import { Save, X } from 'lucide-react';

const VetNutritionForm = ({ petId, existingRecommendation, planId, onSave, onCancel }) => {
  const [instructions, setInstructions] = useState(existingRecommendation || '');
  const [calories, setCalories] = useState('');
  const [selectedPetId, setSelectedPetId] = useState(petId || '');
  const [pets, setPets] = useState([]);
  const [loadingPets, setLoadingPets] = useState(false);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState('');

  // ดึงข้อมูลสัตว์เลี้ยงทั้งหมด (สำหรับสัตวแพทย์)
  useEffect(() => {
    const fetchPets = async () => {
      if (!petId) { // ถ้าไม่มี petId แสดงว่าต้องให้เลือกสัตว์เลี้ยง
        setLoadingPets(true);
        try {
          const response = await apiClient.get('/pets');
          setPets(response.data || []);
        } catch (err) {
          console.error('Failed to fetch pets:', err);
          setError('ไม่สามารถโหลดข้อมูลสัตว์เลี้ยงได้');
        } finally {
          setLoadingPets(false);
        }
      }
    };

    fetchPets();
  }, [petId]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSaving(true);
    setError('');

    try {
      let response;

      if (planId) {
        // Update existing plan
        response = await apiClient.put(`/nutrition/recommendations/${planId}`, {
          custom_instructions: instructions,
          custom_calories: calories ? parseInt(calories) : null
        });
      } else {
        // Create new plan
        response = await apiClient.post('/nutrition/vet-recommendation', {
          pet_id: petId,
          custom_instructions: instructions,
          custom_calories: calories ? parseInt(calories) : null
        });
      }

      onSave(response.data);
    } catch (err) {
      const errorMessage = err.response?.data?.error || 'ไม่สามารถบันทึกคำแนะนำได้';
      setError(errorMessage);
      console.error('Failed to save vet recommendation:', err);
    } finally {
      setSaving(false);
    }
  };

  return (
    <div className="card mt-6">
      <h3 className="text-lg font-semibold text-gray-900 mb-4">
        {existingRecommendation ? 'แก้ไขคำแนะนำโภชนาการ' : 'เพิ่มคำแนะนำโภชนาการ'}
      </h3>
      <form onSubmit={handleSubmit}>
        {error && <p className="text-red-500 mb-4">{error}</p>}

        <div className="mb-4">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            แคลอรี่ต่อวัน (kcal)
          </label>
          <input
            type="number"
            value={calories}
            onChange={(e) => setCalories(e.target.value)}
            className="form-input w-full"
            placeholder="เช่น 350"
            min="0"
          />
        </div>

        <div className="mb-4">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            คำแนะนำโภชนาการ
          </label>
          <textarea
            value={instructions}
            onChange={(e) => setInstructions(e.target.value)}
            rows="5"
            className="form-textarea w-full"
            placeholder="กรอกคำแนะนำโภชนาการสำหรับสัตว์เลี้ยง..."
            required
          />
        </div>

        <div className="flex justify-end gap-4 mt-4">
          <button type="button" onClick={onCancel} className="btn-secondary">
            <X size={18} className="mr-2" />
            ยกเลิก
          </button>
          <button type="submit" className="btn-primary" disabled={saving}>
            <Save size={18} className="mr-2" />
            {saving ? 'กำลังบันทึก...' : (planId ? 'อัปเดตคำแนะนำ' : 'บันทึกคำแนะนำ')}
          </button>
        </div>
      </form>
    </div>
  );
};

export default VetNutritionForm;
