"use client"

import { useState, useEffect, useCallback } from "react"
import { Link } from "react-router-dom"
import { useAuth } from "../contexts/AuthContext"
import { petService, notificationService, appointmentService } from "../services"
import { useApi } from "../hooks"
import { Plus, Heart, Bell, Calendar, PawPrint } from "lucide-react"
import AddPetModal from "../components/AddPetModal"

const Dashboard = () => {
  const { user } = useAuth()
  const [pets, setPets] = useState([])
  const [notifications, setNotifications] = useState([])
  const [todayNotifications, setTodayNotifications] = useState([])
  const [nextAppointment, setNextAppointment] = useState(null)
  const [showAddPetModal, setShowAddPetModal] = useState(false)
  const { loading, execute } = useApi()

  const fetchDashboardData = useCallback(async () => {
    if (!user) {
      console.log("No user found, skipping fetchDashboardData")
      return
    }

    setPets([])
    setNotifications([])
    setTodayNotifications([])
    setNextAppointment(null)

    try {
      // Fetch pets
      const petsResult = await execute(() => petService.getPets(), {
        showError: true,
        resetError: false
      })
      if (petsResult?.success) {
        setPets(petsResult.pets || [])
      }

      // Fetch notifications
      const notificationsResult = await execute(
        () => notificationService.getNotifications(),
        { showError: true, resetError: false }
      )
      if (notificationsResult?.success) {
        const allNotifications = notificationsResult.notifications || []
        setNotifications(allNotifications)

        // กรองเฉพาะของวันนี้
        const today = new Date()
        today.setHours(0, 0, 0, 0)

        const filtered = allNotifications.filter((n) => {
          if (!n.due_date) return false
          const notificationDate = new Date(n.due_date)
          notificationDate.setHours(0, 0, 0, 0)
          return (
            notificationDate.getTime() === today.getTime() &&
            !n.is_completed
          )
        })

        setTodayNotifications(filtered)
      }

      // Fetch appointments
      const appointmentsResult = await execute(
        () => appointmentService.getAppointments(),
        { showError: true, resetError: false }
      )
      if (appointmentsResult?.success) {
        const today = new Date()
        const scheduledAppointments =
          appointmentsResult.appointments?.filter(
            (a) =>
              a.status === "scheduled" &&
              new Date(a.appointment_date) >= today
          ) || []

        scheduledAppointments.sort(
          (a, b) =>
            new Date(a.appointment_date) - new Date(b.appointment_date)
        )
        setNextAppointment(scheduledAppointments[0] || null)
      }
    } catch (err) {
      console.error("Dashboard fetch error:", err)
    }
  }, [user, execute])

  useEffect(() => {
    fetchDashboardData()
  }, [fetchDashboardData])

  const handlePetAdded = () => {
    setShowAddPetModal(false)
    fetchDashboardData()
  }

  if (loading && pets.length === 0) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-green-500"></div>
      </div>
    )
  }

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900">
            สวัสดี,{" "}
            <span className="text-green-600 font-bold">{user?.full_name}</span>
          </h1>
          <p className="text-gray-600 mt-2">
            ยินดีต้อนรับสู่แดชบอร์ดการดูแลสัตว์เลี้ยงของคุณ
          </p>
        </div>

        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
          <div className="card">
            <div className="flex items-center">
              <div className="w-12 h-12 bg-green-100 rounded-full flex items-center justify-center">
                <PawPrint className="h-6 w-6 text-green-500" />
              </div>
              <div className="ml-4">
                <p className="text-sm text-gray-600">สัตว์เลี้ยงของฉัน</p>
                <p className="text-2xl font-bold text-gray-900">
                  {pets.length} ตัว
                </p>
              </div>
            </div>
          </div>

          <div className="card">
            <div className="flex items-center">
              <div className="w-12 h-12 bg-yellow-100 rounded-full flex items-center justify-center">
                <Bell className="h-6 w-6 text-yellow-500" />
              </div>
              <div className="ml-4">
                <p className="text-sm text-gray-600">การแจ้งเตือนวันนี้</p>
                <p className="text-2xl font-bold text-gray-900">
                  {todayNotifications.length} รายการ
                </p>
              </div>
            </div>
          </div>

          <div className="card">
            <div className="flex items-center">
              <div className="w-12 h-12 bg-blue-100 rounded-full flex items-center justify-center">
                <Calendar className="h-6 w-6 text-blue-500" />
              </div>
              <div className="ml-4">
                <p className="text-sm text-gray-600">นัดหมายถัดไป</p>
                {nextAppointment ? (
                  <div>
                    <p className="text-xl font-bold text-gray-900">
                      {new Date(
                        nextAppointment.appointment_date
                      ).toLocaleString("th-TH", {
                        dateStyle: "short",
                        timeStyle: "short"
                      })}
                    </p>
                    <p className="text-sm text-gray-500">
                      {nextAppointment.pet?.name} -{" "}
                      {nextAppointment.appointment_type}
                    </p>
                  </div>
                ) : (
                  <p className="text-2xl font-bold text-gray-900">-</p>
                )}
              </div>
            </div>
          </div>
        </div>

        <div className="grid lg:grid-cols-3 gap-8">
          {/* My Pets Section */}
          <div className="lg:col-span-2">
            <div className="flex justify-between items-center mb-6">
              <h2 className="text-2xl font-bold text-gray-900">สัตว์เลี้ยงของฉัน</h2>
              <button
                onClick={() => setShowAddPetModal(true)}
                className="btn-primary flex items-center space-x-2"
              >
                <Plus className="h-4 w-4" />
                <span>เพิ่มสัตว์เลี้ยงใหม่</span>
              </button>
            </div>

            {pets.length === 0 ? (
              <div className="card text-center py-12">
                <PawPrint className="h-16 w-16 text-gray-300 mx-auto mb-4" />
                <h3 className="text-lg font-medium text-gray-900 mb-2">
                  ยังไม่มีสัตว์เลี้ยง
                </h3>
                <p className="text-gray-600 mb-4">
                  เพิ่มสัตว์เลี้ยงแรกของคุณเพื่อเริ่มต้นการดูแล
                </p>
                <button
                  onClick={() => setShowAddPetModal(true)}
                  className="btn-primary"
                >
                  เพิ่มสัตว์เลี้ยงใหม่
                </button>
              </div>
            ) : (
              <div className="grid md:grid-cols-2 gap-6">
                {pets.map((pet) => (
                  <Link
                    key={pet.id}
                    to={`/pets/${pet.id}`}
                    className="card hover:shadow-lg transition-shadow"
                  >
                    <div className="flex items-center space-x-4">
                      <div className="w-16 h-16 bg-gray-200 rounded-full flex items-center justify-center">
                        <Heart className="h-8 w-8 text-gray-400" />
                      </div>
                      <div className="flex-1">
                        <h3 className="text-lg font-semibold text-gray-900">
                          {pet.name}
                        </h3>
                        <p className="text-gray-600">
                          {pet.species} • {pet.breed || "-"}
                        </p>
                        <p className="text-sm text-gray-500">
                          น้ำหนัก: {pet.weight ? `${pet.weight} กก.` : "-"}
                        </p>
                      </div>
                    </div>
                  </Link>
                ))}
              </div>
            )}
          </div>

          {/* Notifications Section */}
          <div>
            <div className="flex justify-between items-center mb-6">
              <h2 className="text-2xl font-bold text-gray-900">การแจ้งเตือน</h2>
              <Link
                to="/notifications"
                className="text-green-500 hover:text-green-600"
              >
                ดูทั้งหมด
              </Link>
            </div>

            <div className="space-y-4">
              {notifications.length === 0 ? (
                <div className="card text-center py-8">
                  <Bell className="h-12 w-12 text-gray-300 mx-auto mb-3" />
                  <p className="text-gray-600">ไม่มีการแจ้งเตือน</p>
                  <p className="text-sm text-gray-500">ทุกอย่างเป็นไปตามกำหนด</p>
                </div>
              ) : (
                notifications.slice(0, 5).map((notification) => (
                  <div key={notification.id} className="card">
                    <div className="flex items-start space-x-3">
                      <div
                        className={`w-3 h-3 rounded-full mt-2 ${
                          notification.priority === "high"
                            ? "bg-red-500"
                            : notification.priority === "medium"
                            ? "bg-yellow-500"
                            : "bg-green-500"
                        }`}
                      />
                      <div className="flex-1">
                        <h4 className="font-medium text-gray-900">
                          {notification.title}
                        </h4>
                        <p className="text-sm text-gray-600 mt-1">
                          {notification.message}
                        </p>
                        {notification.due_date && (
                          <p className="text-xs text-gray-500 mt-2">
                            กำหนด:{" "}
                            {new Date(
                              notification.due_date
                            ).toLocaleDateString("th-TH")}
                          </p>
                        )}
                      </div>
                    </div>
                  </div>
                ))
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Add Pet Modal */}
      {showAddPetModal && (
        <AddPetModal
          onClose={() => setShowAddPetModal(false)}
          onPetAdded={handlePetAdded}
        />
      )}
    </div>
  )
}

export default Dashboard
