import { useState, useEffect } from "react"
import { useParams, useNavigate } from "react-router-dom"
import axios from "axios"

const ArticleForm = () => {
  const { id } = useParams()
  const navigate = useNavigate()
  const [title, setTitle] = useState("")
  const [content, setContent] = useState("")
  const [category, setCategory] = useState("การดูแลทั่วไป")
  const [status, setStatus] = useState("draft")
  const [excerpt, setExcerpt] = useState("")
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState("")

  const isEditing = id !== undefined

  const categories = ["การดูแลทั่วไป", "โภชนาการ", "สุขภาพและโรคภัย", "พฤติกรรม", "การฝึก"]

  useEffect(() => {
    if (isEditing) {
      setLoading(true)
      axios
        .get(`/api/articles/${id}`, {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        })
        .then((response) => {
          const article = response.data
          setTitle(article.title)
          setContent(article.content)
          setCategory(article.category)
          setStatus(article.status)
          setExcerpt(article.excerpt || "")
        })
        .catch(() => setError("ไม่พบบทความ"))
        .finally(() => setLoading(false))
    }
  }, [id, isEditing])

  const handleSubmit = async (e) => {
    e.preventDefault()
    setLoading(true)
    setError("")

    const articleData = { title, content, category, status, excerpt }
    const token = localStorage.getItem("token")

    try {
      if (isEditing) {
        await axios.put(`/api/articles/${id}`, articleData, {
          headers: { Authorization: `Bearer ${token}` },
        })
      } else {
        await axios.post("/api/articles", articleData, {
          headers: { Authorization: `Bearer ${token}` },
        })
      }
      navigate("/admin/articles")
    } catch (err) {
      setError("เกิดข้อผิดพลาดในการบันทึกบทความ")
      console.error(err)
    } finally {
      setLoading(false)
    }
  }

  if (loading && isEditing) {
    return (
      <div className="flex justify-center items-center h-screen">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-green-500"></div>
      </div>
    )
  }

  return (
    <div className="container mx-auto p-6">
      <h1 className="text-3xl font-bold text-gray-800 mb-8">{isEditing ? "แก้ไขบทความ" : "สร้างบทความใหม่"}</h1>

      {error && <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative mb-6">{error}</div>}

      <form onSubmit={handleSubmit} className="space-y-6 bg-white p-8 rounded-lg shadow-md">
        <div>
          <label htmlFor="title" className="block text-sm font-medium text-gray-700">
            หัวข้อ
          </label>
          <input
            type="text"
            id="title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            className="mt-1 block w-full form-input"
            required
          />
        </div>

        <div>
          <label htmlFor="excerpt" className="block text-sm font-medium text-gray-700">
            เกริ่นนำ (ย่อหน้าสั้นๆ)
          </label>
          <textarea
            id="excerpt"
            value={excerpt}
            onChange={(e) => setExcerpt(e.target.value)}
            rows="3"
            className="mt-1 block w-full form-textarea"
            placeholder="สรุปเนื้อหาของบทความโดยย่อ..."
          ></textarea>
        </div>

        <div>
          <label htmlFor="content" className="block text-sm font-medium text-gray-700">
            เนื้อหา
          </label>
          <textarea
            id="content"
            value={content}
            onChange={(e) => setContent(e.target.value)}
            rows="10"
            className="mt-1 block w-full form-textarea"
            required
          ></textarea>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div>
            <label htmlFor="category" className="block text-sm font-medium text-gray-700">
              หมวดหมู่
            </label>
            <select
              id="category"
              value={category}
              onChange={(e) => setCategory(e.target.value)}
              className="mt-1 block w-full form-select"
            >
              {categories.map((cat) => (
                <option key={cat} value={cat}>
                  {cat}
                </option>
              ))}
            </select>
          </div>
          <div>
            <label htmlFor="status" className="block text-sm font-medium text-gray-700">
              สถานะ
            </label>
            <select
              id="status"
              value={status}
              onChange={(e) => setStatus(e.target.value)}
              className="mt-1 block w-full form-select"
            >
              <option value="draft">ฉบับร่าง</option>
              <option value="published">เผยแพร่</option>
            </select>
          </div>
        </div>

        <div className="flex justify-end gap-4">
          <button type="button" onClick={() => navigate("/admin/articles")} className="btn-secondary">
            ยกเลิก
          </button>
          <button type="submit" className="btn-primary" disabled={loading}>
            {loading ? "กำลังบันทึก..." : "บันทึกบทความ"}
          </button>
        </div>
      </form>
    </div>
  )
}

export default ArticleForm
