"use client"

import { useState, useEffect, useCallback } from "react"
import { Link } from "react-router-dom"
import { useAuth } from "../contexts/AuthContext"
import { petService, notificationService, appointmentService, articleService } from "../services"
import apiClient from "../services/api"
import { useApi } from "../hooks"
import { Plus, Heart, Bell, Calendar, PawPrint, FileText, Users, TrendingUp, Clock, CheckCircle, AlertCircle } from "lucide-react"
import AddPetModal from "../components/AddPetModal"

const Dashboard = () => {
  const { user } = useAuth()
  const [pets, setPets] = useState([])
  const [notifications, setNotifications] = useState([])
  const [appointments, setAppointments] = useState([])
  const [articles, setArticles] = useState([])
  const [stats, setStats] = useState({
    totalPatients: 0,
    todayAppointments: 0,
    pendingAppointments: 0,
    publishedArticles: 0,
    nutritionPlans: 0
  })
  const [showAddPetModal, setShowAddPetModal] = useState(false)
  const [selectedAppointment, setSelectedAppointment] = useState(null)
  const [showAppointmentModal, setShowAppointmentModal] = useState(false)
  const { loading, execute } = useApi()

  const fetchDashboardData = useCallback(async () => {
    if (!user) return;

    setPets([]);
    setNotifications([]);
    setAppointments([]);
    setArticles([]);

    // สำหรับสัตวแพทย์ - ดึงข้อมูลที่เกี่ยวข้องกับงาน
    if (user.role === 'veterinarian') {
      const [appointmentsResult, articlesResult, notificationsResult, nutritionResult] = await Promise.allSettled([
        // เปลี่ยนเป็นใช้ /api/appointments/vet โดยตรง
        execute(() => apiClient.get(`/appointments/vet?t=${Date.now()}`)),
        execute(() => articleService.getMyArticles()),
        execute(() => notificationService.getNotifications()),
        execute(() => apiClient.get('/nutrition/recommendations'))
      ]);

      // ประมวลผลข้อมูลนัดหมาย
      if (appointmentsResult.status === 'fulfilled') {
        let myAppointments = [];

        // Handle different response formats
        if (appointmentsResult.value?.data?.appointments) {
          // Normal response with data
          myAppointments = appointmentsResult.value.data.appointments || [];
        } else if (appointmentsResult.value?.appointments) {
          // Response without nested data
          myAppointments = appointmentsResult.value.appointments || [];
        } else if (Array.isArray(appointmentsResult.value)) {
          // Direct array response
          myAppointments = appointmentsResult.value || [];
        } else if (Array.isArray(appointmentsResult.value?.data)) {
          // Direct array in data
          myAppointments = appointmentsResult.value.data || [];
        }

        setAppointments(myAppointments);

        // คำนวณสถิติ
        const today = new Date();
        today.setHours(0, 0, 0, 0);

        const todayAppts = myAppointments.filter(apt => {
          const aptDate = new Date(apt.appointment_date);
          aptDate.setHours(0, 0, 0, 0);
          return aptDate.getTime() === today.getTime() && apt.status !== 'cancelled';
        });

        const pendingAppts = myAppointments.filter(apt => 
          apt.status === 'scheduled' || apt.status === 'confirmed'
        );

        setStats(prev => ({
          ...prev,
          todayAppointments: todayAppts.length,
          pendingAppointments: pendingAppts.length
        }));
      }

      // ประมวลผลข้อมูลบทความ
      if (articlesResult.status === 'fulfilled' && articlesResult.value?.success) {
        const myArticles = articlesResult.value.articles || [];
        setArticles(myArticles);

        const publishedCount = myArticles.filter(article => article.is_published).length;
        setStats(prev => ({
          ...prev,
          publishedArticles: publishedCount
        }));
      }

      // ประมวลผลข้อมูลแผนโภชนาการ
      if (nutritionResult.status === 'fulfilled' && nutritionResult.value?.data) {
        const nutritionPlans = nutritionResult.value.data || [];
        const myPlans = nutritionPlans.filter(plan => plan.veterinarian_id === user.id);
        setStats(prev => ({
          ...prev,
          nutritionPlans: myPlans.length
        }));
      }

      // ประมวลผลข้อมูลการแจ้งเตือน (เฉพาะที่เกี่ยวข้องกับสัตวแพทย์)
      if (notificationsResult.status === 'fulfilled') {
        let allNotis = [];
        if (Array.isArray(notificationsResult.value)) {
          allNotis = notificationsResult.value;
        } else if (notificationsResult.value?.success && Array.isArray(notificationsResult.value.notifications)) {
          allNotis = notificationsResult.value.notifications;
        } else if (notificationsResult.value?.notifications && Array.isArray(notificationsResult.value.notifications)) {
          allNotis = notificationsResult.value.notifications;
        }

        // กรองเฉพาะการแจ้งเตือนที่เกี่ยวข้องกับสัตวแพทย์
        const vetNotifications = allNotis.filter(notification => 
          !notification.is_completed && 
          (notification.notification_type === 'appointment_reminder' || 
           notification.priority === 'high')
        );

        setNotifications(vetNotifications);
      }

    } else {
      // สำหรับผู้ใช้ทั่วไป - ใช้โค้ดเดิม
      const [petsResult, notificationsResult, appointmentsResult] = await Promise.allSettled([
        execute(() => petService.getPets()),
        execute(() => notificationService.getNotifications()),
        execute(() => appointmentService.getAppointments())
      ]);

      // 1. ประมวลผลข้อมูล Pets
      if (petsResult.status === 'fulfilled' && petsResult.value?.success) {
        setPets(petsResult.value.pets || []);
      }

      // 2. ประมวลผลข้อมูล Notifications
      if (notificationsResult.status === 'fulfilled') {
        let allNotis = [];
        if (Array.isArray(notificationsResult.value)) {
          allNotis = notificationsResult.value;
        } else if (notificationsResult.value?.success && Array.isArray(notificationsResult.value.notifications)) {
          allNotis = notificationsResult.value.notifications;
        } else if (notificationsResult.value?.notifications && Array.isArray(notificationsResult.value.notifications)) {
          allNotis = notificationsResult.value.notifications;
        }

        const sortedNotifications = allNotis
          .filter(notification => !notification.is_completed)
          .sort((a, b) => new Date(a.due_date) - new Date(b.due_date));

        setNotifications(sortedNotifications);
      }

      // 3. ประมวลผลข้อมูล Appointments
      if (appointmentsResult.status === 'fulfilled' && appointmentsResult.value?.success) {
        const allAppointments = appointmentsResult.value.appointments || [];
        const today = new Date();
        today.setHours(0, 0, 0, 0);

        const scheduledAppointments = allAppointments
          .filter(a => {
            const appointmentDate = new Date(a.appointment_date);
            const appointmentDateOnly = new Date(appointmentDate);
            appointmentDateOnly.setHours(0, 0, 0, 0);
            return a.status !== "cancelled" && appointmentDateOnly.getTime() >= today.getTime();
          })
          .sort((a, b) => new Date(a.appointment_date) - new Date(b.appointment_date));

        setAppointments(scheduledAppointments);
      }
    }
  }, [user, execute]);

  useEffect(() => {
    if (user) {
      fetchDashboardData();
    }
  }, [user]); // ลบ execute ออกจาก dependency array

  const handlePetAdded = (newPet) => {
    setShowAddPetModal(false);
    fetchDashboardData();
  }

  if (loading && pets.length === 0 && appointments.length === 0) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-green-500"></div>
      </div>
    );
  }

  // แดชบอร์ดสำหรับสัตวแพทย์
  if (user?.role === 'veterinarian') {
    return (
      <div className="min-h-screen bg-gray-50 py-8">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          {/* Header */}
          <div className="mb-8">
            <h1 className="text-3xl font-bold text-gray-900">
              สวัสดี, <span className="text-green-600 font-bold">ดร.{user?.full_name}</span>
            </h1>
            <p className="text-gray-600 mt-2">แดชบอร์ดสำหรับสัตวแพทย์ - จัดการนัดหมายและดูแลผู้ป่วย</p>
          </div>

          {/* Stats Cards */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
            <div className="card">
              <div className="flex items-center">
                <div className="w-12 h-12 bg-blue-100 rounded-full flex items-center justify-center">
                  <Calendar className="h-6 w-6 text-blue-500" />
                </div>
                <div className="ml-4">
                  <p className="text-sm text-gray-600">นัดหมายวันนี้</p>
                  <p className="text-2xl font-bold text-gray-900">{stats.todayAppointments}</p>
                </div>
              </div>
            </div>

            <div className="card">
              <div className="flex items-center">
                <div className="w-12 h-12 bg-yellow-100 rounded-full flex items-center justify-center">
                  <Clock className="h-6 w-6 text-yellow-500" />
                </div>
                <div className="ml-4">
                  <p className="text-sm text-gray-600">รอการยืนยัน</p>
                  <p className="text-2xl font-bold text-gray-900">{stats.pendingAppointments}</p>
                </div>
              </div>
            </div>

            <div className="card">
              <div className="flex items-center">
                <div className="w-12 h-12 bg-green-100 rounded-full flex items-center justify-center">
                  <FileText className="h-6 w-6 text-green-500" />
                </div>
                <div className="ml-4">
                  <p className="text-sm text-gray-600">บทความที่เผยแพร่</p>
                  <p className="text-2xl font-bold text-gray-900">{stats.publishedArticles}</p>
                </div>
              </div>
            </div>

            <div className="card">
              <div className="flex items-center">
                <div className="w-12 h-12 bg-purple-100 rounded-full flex items-center justify-center">
                  <TrendingUp className="h-6 w-6 text-purple-500" />
                </div>
                <div className="ml-4">
                  <p className="text-sm text-gray-600">แผนโภชนาการ</p>
                  <p className="text-2xl font-bold text-gray-900">{stats.nutritionPlans}</p>
                </div>
              </div>
            </div>
          </div>

          <div className="grid lg:grid-cols-3 gap-8">
            {/* Today's Appointments */}
            <div className="lg:col-span-2">
              <div className="flex justify-between items-center mb-6">
                <h2 className="text-2xl font-bold text-gray-900">นัดหมายล่าสุด</h2>
                <Link to="/appointments" className="text-green-500 hover:text-green-600">
                  ดูทั้งหมด
                </Link>
              </div>

              <div className="space-y-4">
                {appointments.filter(apt => apt.status !== 'cancelled').length === 0 ? (
                  <div className="card text-center py-12">
                    <Calendar className="h-16 w-16 text-gray-300 mx-auto mb-4" />
                    <h3 className="text-lg font-medium text-gray-900 mb-2">ไม่มีนัดหมาย</h3>
                    <p className="text-gray-600">ยังไม่มีนัดหมายที่ต้องดูแล</p>
                  </div>
                ) : (
                  appointments
                    .filter(apt => apt.status !== 'cancelled')
                    .slice(0, 5)
                    .map((appointment) => (
                      <div key={appointment.id} className="card">
                        <div className="flex items-center justify-between">
                          <div className="flex items-center space-x-4">
                            <div className={`w-3 h-3 rounded-full ${
                              appointment.status === 'confirmed' ? 'bg-green-500' : 'bg-yellow-500'
                            }`} />
                            <div>
                              <h4 className="font-medium text-gray-900">
                                {appointment.pets?.name || 'N/A'} - {appointment.appointment_type}
                              </h4>
                              <p className="text-sm text-gray-600">
                                {new Date(appointment.appointment_date).toLocaleString("th-TH", {
                                  dateStyle: 'short',
                                  timeStyle: 'short'
                                })}
                              </p>
                              <p className="text-xs text-gray-500">
                                เจ้าของ: {appointment.user?.email || 'N/A'}
                              </p>
                            </div>
                          </div>
                          <div className="flex space-x-2">
                            <button 
                              onClick={() => {
                                setSelectedAppointment(appointment);
                                setShowAppointmentModal(true);
                              }}
                              className="btn-secondary btn-sm"
                            >
                              ดูรายละเอียด
                            </button>
                          </div>
                        </div>
                      </div>
                    ))
                )}
              </div>
            </div>

            {/* Quick Actions & Notifications */}
            <div className="space-y-8">
              {/* Quick Actions */}
              <div>
                <h2 className="text-2xl font-bold text-gray-900 mb-6">การทำงานด่วน</h2>
                <div className="space-y-3">
                  <Link to="/articles/new" className="card hover:shadow-lg transition-shadow cursor-pointer">
                    <div className="flex items-center space-x-3">
                      <div className="w-10 h-10 bg-green-100 rounded-full flex items-center justify-center">
                        <FileText className="h-5 w-5 text-green-500" />
                      </div>
                      <div>
                        <h3 className="font-medium text-gray-900">เขียนบทความใหม่</h3>
                        <p className="text-sm text-gray-600">แชร์ความรู้กับเจ้าของสัตว์เลี้ยง</p>
                      </div>
                    </div>
                  </Link>

                  <Link to="/nutrition/guidelines" className="card hover:shadow-lg transition-shadow cursor-pointer">
                    <div className="flex items-center space-x-3">
                      <div className="w-10 h-10 bg-blue-100 rounded-full flex items-center justify-center">
                        <Heart className="h-5 w-5 text-blue-500" />
                      </div>
                      <div>
                        <h3 className="font-medium text-gray-900">จัดการโภชนาการ</h3>
                        <p className="text-sm text-gray-600">สร้างคำแนะนำโภชนาการ</p>
                      </div>
                    </div>
                  </Link>

                  <Link to="/appointments" className="card hover:shadow-lg transition-shadow cursor-pointer">
                    <div className="flex items-center space-x-3">
                      <div className="w-10 h-10 bg-purple-100 rounded-full flex items-center justify-center">
                        <Calendar className="h-5 w-5 text-purple-500" />
                      </div>
                      <div>
                        <h3 className="font-medium text-gray-900">จัดการนัดหมาย</h3>
                        <p className="text-sm text-gray-600">ดูและยืนยันนัดหมายทั้งหมด</p>
                      </div>
                    </div>
                  </Link>
                </div>
              </div>

              {/* Recent Articles */}
              <div>
                <div className="flex justify-between items-center mb-6">
                  <h2 className="text-2xl font-bold text-gray-900">บทความล่าสุด</h2>
                  <Link to="/articles" className="text-green-500 hover:text-green-600">
                    ดูทั้งหมด
                  </Link>
                </div>

                <div className="space-y-4">
                  {articles.length === 0 ? (
                    <div className="card text-center py-8">
                      <FileText className="h-12 w-12 text-gray-300 mx-auto mb-3" />
                      <p className="text-gray-600">ยังไม่มีบทความ</p>
                      <Link to="/articles/new" className="text-green-500 hover:text-green-600 text-sm">
                        เขียนบทความแรก
                      </Link>
                    </div>
                  ) : (
                    articles.slice(0, 3).map((article) => (
                      <div key={article.id} className="card">
                        <div className="flex items-start space-x-3">
                          <div className={`w-2 h-2 rounded-full mt-2 ${
                            article.is_published ? 'bg-green-500' : 'bg-yellow-500'
                          }`} />
                          <div className="flex-1">
                            <h4 className="font-medium text-gray-900 line-clamp-2">{article.title}</h4>
                            <p className="text-sm text-gray-600 mt-1 line-clamp-2">{article.excerpt}</p>
                            <div className="flex items-center justify-between mt-2">
                              <span className="text-xs text-gray-500">
                                {new Date(article.created_at).toLocaleDateString("th-TH")}
                              </span>
                              <span className={`text-xs px-2 py-1 rounded-full ${
                                article.is_published 
                                  ? 'bg-green-100 text-green-800' 
                                  : 'bg-yellow-100 text-yellow-800'
                              }`}>
                                {article.is_published ? 'เผยแพร่' : 'ร่าง'}
                              </span>
                            </div>
                          </div>
                        </div>
                      </div>
                    ))
                  )}
                </div>
              </div>

              {/* Important Notifications */}
              <div>
                <h2 className="text-2xl font-bold text-gray-900 mb-6">การแจ้งเตือนสำคัญ</h2>
                <div className="space-y-4">
                  {notifications.length === 0 ? (
                    <div className="card text-center py-8">
                      <Bell className="h-12 w-12 text-gray-300 mx-auto mb-3" />
                      <p className="text-gray-600">ไม่มีการแจ้งเตือนสำคัญ</p>
                    </div>
                  ) : (
                    notifications.slice(0, 3).map((notification) => (
                      <div key={notification.id} className="card">
                        <div className="flex items-start space-x-3">
                          <div className={`w-3 h-3 rounded-full mt-2 ${
                            notification.priority === "high" ? "bg-red-500" : "bg-yellow-500"
                          }`} />
                          <div className="flex-1">
                            <h4 className="font-medium text-gray-900">{notification.title}</h4>
                            <p className="text-sm text-gray-600 mt-1">{notification.message}</p>
                            {notification.due_date && (
                              <p className="text-xs text-gray-500 mt-2">
                                กำหนด: {new Date(notification.due_date).toLocaleDateString("th-TH")}
                              </p>
                            )}
                          </div>
                        </div>
                      </div>
                    ))
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  // แดชบอร์ดสำหรับผู้ใช้ทั่วไป (โค้ดเดิม)
  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900">
            สวัสดี, <span className="text-green-600 font-bold">{user?.full_name}</span>
          </h1>
          <p className="text-gray-600 mt-2">ยินดีต้อนรับสู่แดชบอร์ดการดูแลสัตว์เลี้ยงของคุณ</p>
        </div>

        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
          <div className="card">
            <div className="flex items-center">
              <div className="w-12 h-12 bg-green-100 rounded-full flex items-center justify-center">
                <PawPrint className="h-6 w-6 text-green-500" />
              </div>
              <div className="ml-4">
                <p className="text-sm text-gray-600">สัตว์เลี้ยงของฉัน</p>
                <p className="text-2xl font-bold text-gray-900">{pets.length} ตัว</p>
              </div>
            </div>
          </div>

          <div className="card">
            <div className="flex items-center">
              <div className="w-12 h-12 bg-blue-100 rounded-full flex items-center justify-center">
                <Calendar className="h-6 w-6 text-blue-500" />
              </div>
              <div className="ml-4">
                <p className="text-sm text-gray-600">นัดหมายถัดไป</p>
                {appointments.length > 0 ? (
                  <div>
                    <p className="text-xl font-bold text-gray-900">
                      {new Date(appointments[0].appointment_date).toLocaleString("th-TH", {
                        dateStyle: 'short',
                        timeStyle: 'short'
                      })}
                    </p>
                    <p className="text-sm text-gray-500">
                      {appointments[0].pets?.name || 'N/A'} - {appointments[0].appointment_type}
                    </p>
                  </div>
                ) : (
                  <p className="text-2xl font-bold text-gray-900">-</p>
                )}
              </div>
            </div>
          </div>

          <Link to="/appointments" className="card hover:shadow-lg transition-shadow cursor-pointer">
            <div className="flex items-center">
              <div className="w-12 h-12 bg-purple-100 rounded-full flex items-center justify-center">
                <Calendar className="h-6 w-6 text-purple-500" />
              </div>
              <div className="ml-4">
                <p className="text-sm text-gray-600">นัดหมาย</p>
                <p className="text-lg font-bold text-gray-900">จัดการนัดหมาย</p>
              </div>
            </div>
          </Link>
        </div>

        <div className="grid lg:grid-cols-3 gap-8">
          {/* My Pets Section */}
          <div className="lg:col-span-2">
            <div className="flex justify-between items-center mb-6">
              <h2 className="text-2xl font-bold text-gray-900">สัตว์เลี้ยงของฉัน</h2>
              <button onClick={() => setShowAddPetModal(true)} className="btn-primary flex items-center space-x-2">
                <Plus className="h-4 w-4" />
                <span>เพิ่มสัตว์เลี้ยงใหม่</span>
              </button>
            </div>

            {pets.length === 0 ? (
              <div className="card text-center py-12">
                <PawPrint className="h-16 w-16 text-gray-300 mx-auto mb-4" />
                <h3 className="text-lg font-medium text-gray-900 mb-2">ยังไม่มีสัตว์เลี้ยง</h3>
                <p className="text-gray-600 mb-4">เพิ่มสัตว์เลี้ยงแรกของคุณเพื่อเริ่มต้นการดูแล</p>
                <button onClick={() => setShowAddPetModal(true)} className="btn-primary">
                  เพิ่มสัตว์เลี้ยงใหม่
                </button>
              </div>
            ) : (
              <div className="grid md:grid-cols-2 gap-6">
                {pets.map((pet) => (
                  <Link key={pet.id} to={`/pets/${pet.id}`} className="card hover:shadow-lg transition-shadow">
                    <div className="flex items-center space-x-4">
                      <div className="w-16 h-16 bg-gray-200 rounded-full flex items-center justify-center">
                        <Heart className="h-8 w-8 text-gray-400" />
                      </div>
                      <div className="flex-1">
                        <h3 className="text-lg font-semibold text-gray-900">{pet.name}</h3>
                        <p className="text-gray-600">
                          {pet.species} • {pet.breed}
                        </p>
                      </div>
                    </div>
                  </Link>
                ))}
              </div>
            )}
          </div>

          {/* Notifications Section */}
          <div>
            <div className="flex justify-between items-center mb-6">
              <h2 className="text-2xl font-bold text-gray-900">การแจ้งเตือน</h2>
              <Link to="/notifications" className="text-green-500 hover:text-green-600">
                ดูทั้งหมด
              </Link>
            </div>

            <div className="space-y-4">
              {notifications.length === 0 ? (
                <div className="card text-center py-8">
                  <Bell className="h-12 w-12 text-gray-300 mx-auto mb-3" />
                  <p className="text-gray-600">ไม่มีการแจ้งเตือน</p>
                  <p className="text-sm text-gray-500">ทุกอย่างเป็นไปตามกำหนด</p>
                </div>
              ) : (
                notifications.slice(0, 3).map((notification) => (
                  <div key={notification.id} className="card">
                    <div className="flex items-start space-x-3">
                      <div
                        className={`w-3 h-3 rounded-full mt-2 ${notification.priority === "high"
                            ? "bg-red-500"
                            : notification.priority === "medium"
                              ? "bg-yellow-500"
                              : "bg-green-500"
                          }`}
                      />
                      <div className="flex-1">
                        <h4 className="font-medium text-gray-900">{notification.title}</h4>
                        <p className="text-sm text-gray-600 mt-1">{notification.message}</p>
                        {notification.due_date && (
                          <p className="text-xs text-gray-500 mt-2">
                            กำหนด: {new Date(notification.due_date).toLocaleDateString("th-TH")}
                          </p>
                        )}
                      </div>
                    </div>
                  </div>
                ))
              )}
            </div>
          </div>

          {/* Add Pet Modal */}
          {showAddPetModal && <AddPetModal onClose={() => setShowAddPetModal(false)} onPetAdded={handlePetAdded} />}

          {/* Appointment Detail Modal */}
          {showAppointmentModal && selectedAppointment && (
            <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
              <div className="bg-white rounded-xl max-w-md w-full">
                <div className="flex items-center justify-between p-6 border-b">
                  <h2 className="text-xl font-bold text-gray-900">รายละเอียดนัดหมาย</h2>
                  <button onClick={() => setShowAppointmentModal(false)} className="text-gray-400 hover:text-gray-600">
                    ✕
                  </button>
                </div>
                <div className="p-6 space-y-4">
                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <p className="font-semibold text-gray-700">สัตว์เลี้ยง</p>
                      <p className="text-gray-900">{selectedAppointment.pets?.name || 'ไม่ระบุ'}</p>
                    </div>
                    <div>
                      <p className="font-semibold text-gray-700">ประเภทสัตว์</p>
                      <p className="text-gray-900">{selectedAppointment.pets?.species || 'ไม่ระบุ'} - {selectedAppointment.pets?.breed || 'ไม่ระบุ'}</p>
                    </div>
                    <div>
                      <p className="font-semibold text-gray-700">สี</p>
                      <p className="text-gray-900">{selectedAppointment.pets?.color || 'ไม่ระบุ'}</p>
                    </div>
                    <div>
                      <p className="font-semibold text-gray-700">น้ำหนัก</p>
                      <p className="text-gray-900">{selectedAppointment.pets?.weight ? `${selectedAppointment.pets.weight} กก.` : 'ไม่ระบุ'}</p>
                    </div>
                  </div>

                  <div className="border-t pt-4">
                    <p className="font-semibold text-gray-700">เจ้าของสัตว์เลี้ยง</p>
                    <p className="text-gray-900">{selectedAppointment.user?.full_name || selectedAppointment.user?.email || 'ไม่ระบุ'}</p>
                  </div>

                  <div className="border-t pt-4">
                    <p className="font-semibold text-gray-700">สัตวแพทย์</p>
                    <p className="text-gray-900">{selectedAppointment.veterinarian?.full_name || selectedAppointment.veterinarian?.email || 'ไม่ระบุ'}</p>
                  </div>

                  <div className="border-t pt-4">
                    <p className="font-semibold text-gray-700">วันที่และเวลา</p>
                    <p className="text-gray-900">{new Date(selectedAppointment.appointment_date).toLocaleString('th-TH')}</p>
                  </div>

                  <div className="border-t pt-4">
                    <p className="font-semibold text-gray-700">ประเภทการนัดหมาย</p>
                    <p className="text-gray-900">{selectedAppointment.appointment_type}</p>
                  </div>

                  <div className="border-t pt-4">
                    <p className="font-semibold text-gray-700">สถานะ</p>
                    <span className={`px-3 py-1 rounded-full text-sm font-medium ${
                      selectedAppointment.status === 'scheduled' ? 'bg-yellow-100 text-yellow-800' :
                      selectedAppointment.status === 'confirmed' ? 'bg-green-100 text-green-800' :
                      selectedAppointment.status === 'completed' ? 'bg-gray-100 text-gray-800' :
                      'bg-red-100 text-red-800'
                    }`}>
                      {selectedAppointment.status === 'scheduled' ? 'รอยืนยัน' :
                       selectedAppointment.status === 'confirmed' ? 'ยืนยันแล้ว' :
                       selectedAppointment.status === 'completed' ? 'เสร็จสิ้น' : 'ยกเลิก'}
                    </span>
                  </div>

                  {selectedAppointment.notes && (
                    <div className="border-t pt-4">
                      <p className="font-semibold text-gray-700">หมายเหตุ</p>
                      <p className="text-gray-900">{selectedAppointment.notes}</p>
                    </div>
                  )}

                  <div className="border-t pt-4 text-xs text-gray-500">
                    <p>สร้างเมื่อ: {new Date(selectedAppointment.created_at).toLocaleString('th-TH')}</p>
                    {selectedAppointment.updated_at && selectedAppointment.updated_at !== selectedAppointment.created_at && (
                      <p>อัปเดตล่าสุด: {new Date(selectedAppointment.updated_at).toLocaleString('th-TH')}</p>
                    )}
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default Dashboard
