"use client"

import { useState, useEffect } from "react"
import { useNavigate } from "react-router-dom"
import { useAuth } from "../contexts/AuthContext"
import { uploadService } from "../services"
import { User, Mail, Phone, Home, Camera } from "lucide-react"

const EditProfile = () => {
  const { user, updateProfile, loading: authLoading } = useAuth()
  const navigate = useNavigate()

  const [formData, setFormData] = useState({
    full_name: "",
    email: "",
    phone: "",
    address: "",
    profile_picture_url: "",
  })
  const [newProfileImage, setNewProfileImage] = useState(null)
  const [previewImage, setPreviewImage] = useState("")
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState("")

  useEffect(() => {
    if (user) {
      setFormData({
        full_name: user.full_name || "",
        email: user.email || "",
        phone: user.phone || "",
        address: user.address || "",
        profile_picture_url: user.profile_picture_url || "",
      });
      // ป้องกัน loop: setPreviewImage เฉพาะเมื่อค่าต่างกันจริง ๆ
      if (user.profile_picture_url && user.profile_picture_url !== previewImage) {
        setPreviewImage(user.profile_picture_url);
      } else if (!user.profile_picture_url && previewImage !== "") {
        setPreviewImage("");
      }
    }
    // eslint-disable-next-line
  }, [user]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value })
  }

  const handleImageChange = async (e) => {
    if (e.target.files && e.target.files[0]) {
      const file = e.target.files[0];
      // ไม่ต้อง setLoading(true) ที่นี่ เพราะการอัปเดตจะเกิดตอน submit
      setError("");

      try {
        // 1. อัปโหลดไฟล์เพื่อรับ URL
        const uploadRes = await uploadService.uploadFile(file);

        if (uploadRes && uploadRes.file && uploadRes.file.url) {
          const newImageUrl = uploadRes.file.url;
          // 2. อัปเดต State ของ form และ preview เท่านั้น
          setFormData((prev) => ({ ...prev, profile_picture_url: newImageUrl }));
          setPreviewImage(newImageUrl); // แสดงรูปใหม่ทันที
        } else {
          setError(uploadRes?.error || "Failed to get image URL.");
        }
      } catch (err) {
        console.error("Image Upload Error:", err);
        setError("Failed to upload image. Please try again.");
      }
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");

    // ส่ง formData ที่มีข้อมูลล่าสุดทั้งหมด (รวม URL รูปใหม่ถ้ามี)
    const updateResult = await updateProfile(formData);

    if (updateResult.success) {
      // ไม่จำเป็นต้อง setPreviewImage อีก เพราะทำใน handleImageChange แล้ว
      navigate("/dashboard");
    } else {
      setError(updateResult.error || "Failed to update profile.");
    }

    setLoading(false);
  }

  if (authLoading || !user) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-green-500"></div>
      </div>
    )
  }

  return (
    <div className="min-h-screen bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-2xl mx-auto">
        <div className="bg-white p-8 rounded-2xl shadow-lg">
          <h2 className="text-3xl font-bold text-gray-900 text-center mb-8">แก้ไขข้อมูลส่วนตัว</h2>

          <form onSubmit={handleSubmit} className="space-y-6">
            {error && <div className="bg-red-50 border border-red-200 text-red-600 px-4 py-3 rounded-lg">{error}</div>}

            <div className="flex flex-col items-center space-y-4">
              <div className="relative">
                {previewImage ? (
                  <img
                    src={previewImage}
                    alt="Profile"
                    className="w-32 h-32 rounded-full object-cover border-4 border-gray-200"
                  />
                ) : (
                  // ถ้ามีไฟล์ placeholder-user.jpg ใน public ให้ใช้ path ที่ถูกต้อง เช่น /images/placeholder-user.jpg
                  // ถ้าไม่มีไฟล์ ให้ไม่แสดงรูปเลย
                  null
                )}
                <label
                  htmlFor="profile_picture"
                  className="absolute bottom-0 right-0 bg-green-500 text-white p-2 rounded-full cursor-pointer hover:bg-green-600 transition-colors"
                >
                  <Camera className="h-5 w-5" />
                  <input
                    id="profile_picture"
                    name="profile_picture"
                    type="file"
                    accept="image/*"
                    className="hidden"
                    onChange={handleImageChange}
                  />
                </label>
              </div>
            </div>

            {/* Full Name */}
            <div>
              <label htmlFor="full_name" className="block text-sm font-medium text-gray-700 mb-1">
                ชื่อ-นามสกุล
              </label>
              <div className="relative">
                <User className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
                <input
                  id="full_name"
                  name="full_name"
                  type="text"
                  required
                  className="form-input pl-10"
                  placeholder="กรอกชื่อ-นามสกุลของคุณ"
                  value={formData.full_name}
                  onChange={handleChange}
                />
              </div>
            </div>

            {/* Email (Read-only) */}
            <div>
              <label htmlFor="email" className="block text-sm font-medium text-gray-700 mb-1">
                อีเมล
              </label>
              <div className="relative">
                <Mail className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
                <input
                  id="email"
                  name="email"
                  type="email"
                  readOnly
                  className="form-input pl-10 bg-gray-100 cursor-not-allowed"
                  value={formData.email}
                />
              </div>
            </div>

            {/* Phone */}
            <div>
              <label htmlFor="phone" className="block text-sm font-medium text-gray-700 mb-1">
                เบอร์โทรศัพท์
              </label>
              <div className="relative">
                <Phone className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
                <input
                  id="phone"
                  name="phone"
                  type="tel"
                  className="form-input pl-10"
                  placeholder="กรอกเบอร์โทรศัพท์"
                  value={formData.phone}
                  onChange={handleChange}
                />
              </div>
            </div>

            {/* Address */}
            <div>
              <label htmlFor="address" className="block text-sm font-medium text-gray-700 mb-1">
                ที่อยู่
              </label>
              <div className="relative">
                <Home className="absolute left-3 top-4 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
                <textarea
                  id="address"
                  name="address"
                  rows="3"
                  className="form-input pl-10"
                  placeholder="กรอกที่อยู่ของคุณ"
                  value={formData.address}
                  onChange={handleChange}
                ></textarea>
              </div>
            </div>

            {/* Submit Button */}
            <div className="flex justify-end space-x-4 pt-4">
              <button
                type="button"
                onClick={() => navigate("/dashboard")}
                className="btn-secondary"
                disabled={loading}
              >
                ยกเลิก
              </button>
              <button
                type="submit"
                disabled={loading}
                className="btn-primary disabled:opacity-50 disabled:cursor-not-allowed"
              >
                {loading ? "กำลังบันทึก..." : "บันทึกการเปลี่ยนแปลง"}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  )
}

export default EditProfile
