"use client"

import { useState, useEffect } from "react"
import { useParams, Link } from "react-router-dom"
import { petService } from "../services"
import { useAuth } from "../contexts/AuthContext"
import { useApi } from "../hooks"
import { ArrowLeft, Utensils, Brain, AlertCircle, CheckCircle, Edit } from "lucide-react"
import axios from "axios"
import VetNutritionForm from "../components/VetNutritionForm"

const NutritionRecommendation = () => {
  const { petId } = useParams()
  const { user } = useAuth()
  const [pet, setPet] = useState(null)
  const [vetRecommendation, setVetRecommendation] = useState(null)
  const [aiRecommendation, setAiRecommendation] = useState(null)
  const [generating, setGenerating] = useState(false)
  const [showVetForm, setShowVetForm] = useState(false)
  const { loading, execute } = useApi()

  useEffect(() => {
    if (petId) {
      fetchPetData()
      fetchVetRecommendation()
    }
  }, [petId])

  const fetchPetData = async () => {
    const result = await execute(
      () => petService.getPet(petId),
      {
        onSuccess: (result) => setPet(result.pet),
        onError: (error) => console.error("Failed to fetch pet data:", error)
      }
    )
  }

  const fetchVetRecommendation = async () => {
    try {
        const token = localStorage.getItem("token");
        const response = await axios.get(`/api/nutrition/recommendations?pet_id=${petId}`, {
            headers: { Authorization: `Bearer ${token}` },
        });
        if (response.data && response.data.length > 0) {
            setVetRecommendation(response.data[0]);
        }
    } catch (error) {
        console.error("Failed to fetch vet recommendation:", error);
    }
  }

  const generateRecommendation = async () => {
    setGenerating(true)
    try {
      const token = localStorage.getItem("token");
      const response = await axios.post("/api/ai/nutrition-recommendation",
        { pet_id: petId },
        { headers: { Authorization: `Bearer ${token}` } }
      )
      setAiRecommendation(response.data)
    } catch (error) {
      console.error("Failed to generate recommendation:", error)
    } finally {
      setGenerating(false)
    }
  }

  const handleVetRecSaved = (savedRec) => {
    setVetRecommendation(savedRec);
    setShowVetForm(false);
  }

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-green-500"></div>
      </div>
    )
  }

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Back Button */}
        <Link
          to={`/pets/${petId}`}
          className="inline-flex items-center space-x-2 text-green-500 hover:text-green-600 mb-8"
        >
          <ArrowLeft className="h-4 w-4" />
          <span>กลับไปหน้าสัตว์เลี้ยง</span>
        </Link>

        {/* Header */}
        <div className="card mb-8">
          <div className="flex items-center space-x-4">
            <div className="w-16 h-16 bg-orange-100 rounded-full flex items-center justify-center">
              <Utensils className="h-8 w-8 text-orange-500" />
            </div>
            <div>
              <h1 className="text-3xl font-bold text-gray-900">คำแนะนำโภชนาการ</h1>
              <p className="text-gray-600">สำหรับ {pet?.name}</p>
            </div>
          </div>
        </div>

        {/* Vet Recommendation */}
        {vetRecommendation && (
            <div className="card border-l-4 border-green-500">
                <div className="flex justify-between items-start">
                    <div>
                        <h3 className="text-lg font-semibold text-gray-900 mb-2">คำแนะนำจากสัตวแพทย์</h3>
                        <p className="text-gray-700 leading-relaxed whitespace-pre-line">{vetRecommendation.custom_instructions}</p>
                    </div>
                    {user?.role === 'veterinarian' && !showVetForm && (
                        <button onClick={() => setShowVetForm(true)} className="btn-secondary btn-sm">
                            <Edit size={16} />
                        </button>
                    )}
                </div>
            </div>
        )}

        {/* Vet Form */}
        {user?.role === 'veterinarian' && showVetForm && (
            <VetNutritionForm
                petId={petId}
                existingRecommendation={vetRecommendation?.custom_instructions}
                onSave={handleVetRecSaved}
                onCancel={() => setShowVetForm(false)}
            />
        )}

        {/* AI Recommendation */}
        {!vetRecommendation && !aiRecommendation && (
          <div className="card text-center py-12">
            <Brain className="h-16 w-16 text-gray-300 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">สร้างคำแนะนำโภชนาการ</h3>
            <p className="text-gray-600 mb-6">ใช้ AI เพื่อวิเคราะห์และสร้างคำแนะนำเบื้องต้น</p>
            <button
              onClick={generateRecommendation}
              disabled={generating}
              className="btn-primary disabled:opacity-50 disabled:cursor-not-allowed"
            >
              {generating ? "กำลังสร้างคำแนะนำ..." : "สร้างคำแนะนำจาก AI"}
            </button>
          </div>
        )}

        {aiRecommendation && !vetRecommendation && (
          <div className="space-y-6 mt-6">
            <div className="card border-l-4 border-blue-500">
                <h3 className="text-lg font-semibold text-gray-900 mb-2">คำแนะนำจาก AI</h3>
                <p className="text-gray-700 leading-relaxed">{aiRecommendation.recommendation}</p>
            </div>
            <div className="card">
              <h3 className="text-lg font-semibold text-gray-900 mb-4">ข้อแนะนำการปฏิบัติ</h3>
              <ul className="space-y-2">
                {aiRecommendation.suggested_actions.map((action, index) => (
                  <li key={index} className="flex items-start space-x-2">
                    <CheckCircle className="h-5 w-5 text-green-500 mt-0.5 flex-shrink-0" />
                    <span className="text-gray-700">{action}</span>
                  </li>
                ))}
              </ul>
            </div>
          </div>
        )}

        {/* Add Vet Recommendation Button */}
        {user?.role === 'veterinarian' && !vetRecommendation && !showVetForm && (
             <div className="text-center mt-8">
                <button onClick={() => setShowVetForm(true)} className="btn-outline">
                    <Edit size={18} className="mr-2" />
                    เพิ่มคำแนะนำจากสัตวแพทย์
                </button>
            </div>
        )}
      </div>
    </div>
  )
}

export default NutritionRecommendation
