"use client"

import { useState, useEffect } from "react"
import { useParams, Link } from "react-router-dom"
import { petService } from "../services"
import { useApi } from "../hooks"
import { ArrowLeft, Utensils, Brain, AlertCircle, CheckCircle } from "lucide-react"

const NutritionRecommendation = () => {
  const { petId } = useParams()
  const [pet, setPet] = useState(null)
  const [recommendation, setRecommendation] = useState(null)
  const [loading, setLoading] = useState(true)
  const [generating, setGenerating] = useState(false)

  useEffect(() => {
    fetchPetData()
  }, [petId])

  const fetchPetData = async () => {
    try {
      const response = await axios.get(`/api/pets/${petId}`)
      setPet(response.data)
    } catch (error) {
      console.error("Failed to fetch pet data:", error)
    } finally {
      setLoading(false)
    }
  }

  const generateRecommendation = async () => {
    setGenerating(true)
    try {
      const response = await axios.post("/api/ai/nutrition-recommendation", {
        pet_id: petId,
      })
      setRecommendation(response.data)
    } catch (error) {
      console.error("Failed to generate recommendation:", error)
    } finally {
      setGenerating(false)
    }
  }

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-green-500"></div>
      </div>
    )
  }

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Back Button */}
        <Link
          to={`/pets/${petId}`}
          className="inline-flex items-center space-x-2 text-green-500 hover:text-green-600 mb-8"
        >
          <ArrowLeft className="h-4 w-4" />
          <span>กลับไปหน้าสัตว์เลี้ยง</span>
        </Link>

        {/* Header */}
        <div className="card mb-8">
          <div className="flex items-center space-x-4">
            <div className="w-16 h-16 bg-orange-100 rounded-full flex items-center justify-center">
              <Utensils className="h-8 w-8 text-orange-500" />
            </div>
            <div>
              <h1 className="text-3xl font-bold text-gray-900">คำแนะนำโภชนาการ</h1>
              <p className="text-gray-600">สำหรับ {pet?.name}</p>
            </div>
          </div>
        </div>

        {/* Generate Recommendation */}
        {!recommendation && (
          <div className="card text-center py-12">
            <Brain className="h-16 w-16 text-gray-300 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">สร้างคำแนะนำโภชนาการ</h3>
            <p className="text-gray-600 mb-6">ใช้ AI เพื่อวิเคราะห์และสร้างคำแนะนำโภชนาการที่เหมาะสมสำหรับ {pet?.name}</p>
            <button
              onClick={generateRecommendation}
              disabled={generating}
              className="btn-primary disabled:opacity-50 disabled:cursor-not-allowed"
            >
              {generating ? "กำลังสร้างคำแนะนำ..." : "สร้างคำแนะนำ"}
            </button>
          </div>
        )}

        {/* Recommendation Results */}
        {recommendation && (
          <div className="space-y-6">
            {/* Confidence Score */}
            <div className="card">
              <div className="flex items-center justify-between mb-4">
                <h3 className="text-lg font-semibold text-gray-900">ความน่าเชื่อถือของคำแนะนำ</h3>
                <div className="flex items-center space-x-2">
                  {recommendation.confidence_score >= 0.8 ? (
                    <CheckCircle className="h-5 w-5 text-green-500" />
                  ) : (
                    <AlertCircle className="h-5 w-5 text-yellow-500" />
                  )}
                  <span className="font-medium">{Math.round(recommendation.confidence_score * 100)}%</span>
                </div>
              </div>
              <div className="w-full bg-gray-200 rounded-full h-2">
                <div
                  className={`h-2 rounded-full ${
                    recommendation.confidence_score >= 0.8
                      ? "bg-green-500"
                      : recommendation.confidence_score >= 0.6
                        ? "bg-yellow-500"
                        : "bg-red-500"
                  }`}
                  style={{ width: `${recommendation.confidence_score * 100}%` }}
                ></div>
              </div>
              {recommendation.veterinarian_consultation_needed && (
                <div className="mt-4 p-3 bg-yellow-50 border border-yellow-200 rounded-lg">
                  <p className="text-sm text-yellow-800">
                    <AlertCircle className="h-4 w-4 inline mr-2" />
                    แนะนำให้ปรึกษาสัตวแพทย์เพื่อความแม่นยำ
                  </p>
                </div>
              )}
            </div>

            {/* Main Recommendation */}
            <div className="card">
              <h3 className="text-lg font-semibold text-gray-900 mb-4">คำแนะนำหลัก</h3>
              <p className="text-gray-700 leading-relaxed">{recommendation.recommendation}</p>
            </div>

            {/* Suggested Actions */}
            <div className="card">
              <h3 className="text-lg font-semibold text-gray-900 mb-4">ข้อแนะนำการปฏิบัติ</h3>
              <ul className="space-y-2">
                {recommendation.suggested_actions.map((action, index) => (
                  <li key={index} className="flex items-start space-x-2">
                    <CheckCircle className="h-5 w-5 text-green-500 mt-0.5 flex-shrink-0" />
                    <span className="text-gray-700">{action}</span>
                  </li>
                ))}
              </ul>
            </div>

            {/* Actions */}
            <div className="flex space-x-4">
              <button
                onClick={generateRecommendation}
                disabled={generating}
                className="btn-outline disabled:opacity-50 disabled:cursor-not-allowed"
              >
                {generating ? "กำลังสร้างใหม่..." : "สร้างคำแนะนำใหม่"}
              </button>
              <Link to="/appointments" className="btn-primary">
                จองนัดหมายกับสัตวแพทย์
              </Link>
            </div>
          </div>
        )}
      </div>
    </div>
  )
}

export default NutritionRecommendation
