"use client"

import { useState, useEffect, useCallback } from "react"
import { Link } from "react-router-dom"
import { useAuth } from "../contexts/AuthContext"
import { petService, notificationService, appointmentService } from "../services"
import { useApi } from "../hooks"
import { Plus, Heart, Bell, Calendar, PawPrint } from "lucide-react"
import AddPetModal from "../components/AddPetModal"

// 🔧 Helper: รองรับ response ที่โครงสร้างต่างกัน
const extractArray = (res, key) => {
  if (Array.isArray(res)) return res
  if (Array.isArray(res?.[key])) return res[key]
  if (Array.isArray(res?.data?.[key])) return res.data[key]
  if (Array.isArray(res?.data)) return res.data
  return []
}

const Dashboard = () => {
  const { user } = useAuth()
  const [pets, setPets] = useState([])
  const [notifications, setNotifications] = useState([])
  const [todayNotifications, setTodayNotifications] = useState([])
  const [nextAppointment, setNextAppointment] = useState(null)
  const [showAddPetModal, setShowAddPetModal] = useState(false)
  const { loading, execute } = useApi()

  const fetchDashboardData = useCallback(async () => {
    if (!user) return

    setPets([])
    setNotifications([])
    setTodayNotifications([])
    setNextAppointment(null)

    const ts = Date.now()

    try {
      const [petsRes, notiRes, apptRes] = await Promise.all([
        execute(() => petService.getPets({ params: { _ts: ts } }), { showError: true }),
        execute(() => notificationService.getNotifications({ params: { _ts: ts } }), { showError: true }),
        execute(() => appointmentService.getAppointments({ params: { _ts: ts } }), { showError: true })
      ])

      // Pets
      if (petsRes) {
        const petsArr = extractArray(petsRes, "pets")
        setPets(petsArr)
        console.log("[Dashboard] pets:", petsArr)
      }

      // Notifications
      if (notiRes) {
        const allNotis = extractArray(notiRes, "notifications")
        setNotifications(allNotis)
        console.log("[Dashboard] notifications (all):", allNotis)

        const todayStr = new Date().toISOString().slice(0, 10) // YYYY-MM-DD
        const todays = allNotis.filter((n) => {
          if (!n?.due_date) return false
          const dueStr = new Date(n.due_date).toISOString().slice(0, 10)
          return dueStr === todayStr && !n.is_completed
        })
        setTodayNotifications(todays)
        console.log("[Dashboard] notifications (today):", todays)
      }

      // Appointments
      if (apptRes) {
        const appts = extractArray(apptRes, "appointments")
        console.log("[Dashboard] appointments:", appts)
        const now = new Date()
        const scheduled = appts
          .filter((a) => a?.status === "scheduled" && new Date(a.appointment_date) >= now)
          .sort((a, b) => new Date(a.appointment_date) - new Date(b.appointment_date))
        setNextAppointment(scheduled[0] || null)
      }
    } catch (err) {
      console.error("Dashboard fetch error:", err)
    }
  }, [user, execute])

  useEffect(() => { fetchDashboardData() }, [fetchDashboardData])

  const handlePetAdded = () => {
    setShowAddPetModal(false)
    fetchDashboardData()
  }

  if (loading && pets.length === 0) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-green-500"></div>
      </div>
    )
  }

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900">
            สวัสดี, <span className="text-green-600 font-bold">{user?.full_name}</span>
          </h1>
          <p className="text-gray-600 mt-2">ยินดีต้อนรับสู่แดชบอร์ดการดูแลสัตว์เลี้ยงของคุณ</p>
        </div>

        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
          {/* Pets Card */}
          <div className="card">
            <div className="flex items-center">
              <div className="w-12 h-12 bg-green-100 rounded-full flex items-center justify-center">
                <PawPrint className="h-6 w-6 text-green-500" />
              </div>
              <div className="ml-4">
                <p className="text-sm text-gray-600">สัตว์เลี้ยงของฉัน</p>
                <p className="text-2xl font-bold text-gray-900">{pets.length} ตัว</p>
              </div>
            </div>
          </div>

          {/* Notifications Today Card */}
          <div className="card">
            <div className="flex items-center">
              <div className="w-12 h-12 bg-yellow-100 rounded-full flex items-center justify-center">
                <Bell className="h-6 w-6 text-yellow-500" />
              </div>
              <div className="ml-4">
                <p className="text-sm text-gray-600">การแจ้งเตือนวันนี้</p>
                <p className="text-2xl font-bold text-gray-900">{todayNotifications.length} รายการ</p>
              </div>
            </div>
          </div>

          {/* Next Appointment Card */}
          <div className="card">
            <div className="flex items-center">
              <div className="w-12 h-12 bg-blue-100 rounded-full flex items-center justify-center">
                <Calendar className="h-6 w-6 text-blue-500" />
              </div>
              <div className="ml-4">
                <p className="text-sm text-gray-600">นัดหมายถัดไป</p>
                {nextAppointment ? (
                  <div>
                    <p className="text-xl font-bold text-gray-900">
                      {new Date(nextAppointment.appointment_date).toLocaleString("th-TH", {
                        dateStyle: "short",
                        timeStyle: "short"
                      })}
                    </p>
                    <p className="text-sm text-gray-500">
                      {nextAppointment?.pet?.name || "-"} - {nextAppointment?.appointment_type || "-"}
                    </p>
                  </div>
                ) : (
                  <p className="text-2xl font-bold text-gray-900">-</p>
                )}
              </div>
            </div>
          </div>
        </div>

        <div className="grid lg:grid-cols-3 gap-8">
          {/* My Pets Section */}
          <div className="lg:col-span-2">
            <div className="flex justify-between items-center mb-6">
              <h2 className="text-2xl font-bold text-gray-900">สัตว์เลี้ยงของฉัน</h2>
              <button onClick={() => setShowAddPetModal(true)} className="btn-primary flex items-center space-x-2">
                <Plus className="h-4 w-4" />
                <span>เพิ่มสัตว์เลี้ยงใหม่</span>
              </button>
            </div>

            {pets.length === 0 ? (
              <div className="card text-center py-12">
                <PawPrint className="h-16 w-16 text-gray-300 mx-auto mb-4" />
                <h3 className="text-lg font-medium text-gray-900 mb-2">ยังไม่มีสัตว์เลี้ยง</h3>
                <p className="text-gray-600 mb-4">เพิ่มสัตว์เลี้ยงแรกของคุณเพื่อเริ่มต้นการดูแล</p>
                <button onClick={() => setShowAddPetModal(true)} className="btn-primary">เพิ่มสัตว์เลี้ยงใหม่</button>
              </div>
            ) : (
              <div className="grid md:grid-cols-2 gap-6">
                {pets.map((pet) => (
                  <Link key={pet.id} to={`/pets/${pet.id}`} className="card hover:shadow-lg transition-shadow">
                    <div className="flex items-center space-x-4">
                      <div className="w-16 h-16 bg-gray-200 rounded-full flex items-center justify-center">
                        <Heart className="h-8 w-8 text-gray-400" />
                      </div>
                      <div className="flex-1">
                        <h3 className="text-lg font-semibold text-gray-900">{pet.name}</h3>
                        <p className="text-gray-600">
                          {pet.species} • {pet.breed || "-"}
                        </p>
                        <p className="text-sm text-gray-500">น้ำหนัก: {pet.weight ? `${pet.weight} กก.` : "-"}</p>
                      </div>
                    </div>
                  </Link>
                ))}
              </div>
            )}
          </div>

          {/* Notifications Section */}
          <div>
            <div className="flex justify-between items-center mb-6">
              <h2 className="text-2xl font-bold text-gray-900">การแจ้งเตือน</h2>
              <Link to="/notifications" className="text-green-500 hover:text-green-600">ดูทั้งหมด</Link>
            </div>

            <div className="space-y-4">
              {notifications.length === 0 ? (
                <div className="card text-center py-8">
                  <Bell className="h-12 w-12 text-gray-300 mx-auto mb-3" />
                  <p className="text-gray-600">ไม่มีการแจ้งเตือน</p>
                  <p className="text-sm text-gray-500">ทุกอย่างเป็นไปตามกำหนด</p>
                </div>
              ) : (
                notifications.slice(0, 5).map((n) => (
                  <div key={n.id} className="card">
                    <div className="flex items-start space-x-3">
                      <div
                        className={`w-3 h-3 rounded-full mt-2 ${
                          n.priority === "high" ? "bg-red-500" :
                          n.priority === "medium" ? "bg-yellow-500" : "bg-green-500"
                        }`}
                      />
                      <div className="flex-1">
                        <h4 className="font-medium text-gray-900">{n.title}</h4>
                        <p className="text-sm text-gray-600 mt-1">{n.message}</p>
                        {n.due_date && (
                          <p className="text-xs text-gray-500 mt-2">
                            กำหนด: {new Date(n.due_date).toLocaleDateString("th-TH")}
                          </p>
                        )}
                      </div>
                    </div>
                  </div>
                ))
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Add Pet Modal */}
      {showAddPetModal && (
        <AddPetModal onClose={() => setShowAddPetModal(false)} onPetAdded={handlePetAdded} />
      )}
    </div>
  )
}

export default Dashboard
