"use client"

import { useState, useEffect } from "react"
import { useAuth } from "../contexts/AuthContext"
import apiClient from "../services/api"
import { Calendar, Clock, User, Plus, CheckCircle, XCircle } from "lucide-react"
import BookAppointmentModal from "../components/BookAppointmentModal"

const Appointments = () => {
  const { user } = useAuth()
  const [appointments, setAppointments] = useState([])
  const [loading, setLoading] = useState(true)
  const [showBookModal, setShowBookModal] = useState(false)
  const [selectedAppointment, setSelectedAppointment] = useState(null)
  const [showDetailModal, setShowDetailModal] = useState(false)

  useEffect(() => {
    fetchAppointments()
  }, [])

  const fetchAppointments = async () => {
    try {
      const response = await apiClient.get("/appointments")
      setAppointments(response.data)
    } catch (error) {
      console.error("Failed to fetch appointments:", error)
    } finally {
      setLoading(false)
    }
  }

  const handleAppointmentBooked = (newAppointment) => {
    setAppointments([...appointments, newAppointment])
    setShowBookModal(false)
  }

  const handleUpdateStatus = async (id, status) => {
    try {
      const response = await apiClient.patch(`/appointments/${id}/status`, { status });
      // Update the appointment in the list
      setAppointments(appointments.map(app => app.id === id ? response.data : app));
    } catch (error) {
      console.error(`Failed to update appointment status to ${status}:`, error);
      // Optionally, show an error message to the user
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case "scheduled":
        return "bg-blue-100 text-blue-800"
      case "confirmed":
        return "bg-green-100 text-green-800"
      case "completed":
        return "bg-gray-100 text-gray-800"
      case "cancelled":
        return "bg-red-100 text-red-800"
      default:
        return "bg-gray-100 text-gray-800"
    }
  }

  const getStatusText = (status) => {
    switch (status) {
      case "scheduled":
        return "รอยืนยัน"
      case "confirmed":
        return "ยืนยันแล้ว"
      case "completed":
        return "เสร็จสิ้น"
      case "cancelled":
        return "ยกเลิก"
      default:
        return status
    }
  }

  const getAppointmentTypeText = (type) => {
    switch (type) {
      case "checkup":
        return "ตรวจสุขภาพทั่วไป";
      case "vaccination":
        return "ฉีดวัคซีน";
      case "consultation":
        return "ปรึกษาปัญหา";
      case "surgery":
        return "ผ่าตัด";
      case "emergency":
        return "ฉุกเฉิน";
      default:
        return type;
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-green-500"></div>
      </div>
    )
  }

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="flex justify-between items-center mb-8">
          <div>
            <h1 className="text-3xl font-bold text-gray-900">การนัดหมาย</h1>
            <p className="text-gray-600 mt-2">จัดการนัดหมายกับสัตวแพทย์</p>
          </div>
          <button onClick={() => setShowBookModal(true)} className="btn-primary flex items-center space-x-2">
            <Plus className="h-4 w-4" />
            <span>จองนัดหมาย</span>
          </button>
        </div>

        {/* Appointments List */}
        {appointments.length === 0 ? (
          <div className="card text-center py-12">
            <Calendar className="h-16 w-16 text-gray-300 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">ยังไม่มีการนัดหมาย</h3>
            <p className="text-gray-600 mb-4">จองนัดหมายกับสัตวแพทย์เพื่อดูแลสุขภาพสัตว์เลี้ยงของคุณ</p>
            <button onClick={() => setShowBookModal(true)} className="btn-primary">
              จองนัดหมายแรก
            </button>
          </div>
        ) : (
          <div className="space-y-4">
            {appointments.map((appointment) => (
              <div key={appointment.id} className="card cursor-pointer hover:shadow-md transition-shadow" onClick={() => {
                setSelectedAppointment(appointment);
                setShowDetailModal(true);
              }}>
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-4">
                    <div className="w-12 h-12 bg-blue-100 rounded-full flex items-center justify-center">
                      <Calendar className="h-6 w-6 text-blue-500" />
                    </div>
                    <div>
                      <h3 className="text-lg font-semibold text-gray-900">
                        {appointment.appointment_type === "checkup"
                          ? "ตรวจสุขภาพ"
                          : appointment.appointment_type === "vaccination"
                            ? "ฉีดวัคซีน"
                            : appointment.appointment_type === "consultation"
                              ? "ปรึกษา"
                              : appointment.appointment_type}
                      </h3>
                      <div className="flex items-center space-x-4 text-sm text-gray-600 mt-1">
                        <div className="flex items-center space-x-1">
                          <Calendar className="h-4 w-4" />
                          <span>{new Date(appointment.appointment_date).toLocaleDateString("th-TH")}</span>
                        </div>
                        <div className="flex items-center space-x-1">
                          <Clock className="h-4 w-4" />
                          <span>
                            {new Date(appointment.appointment_date).toLocaleTimeString("th-TH", {
                              hour: "2-digit",
                              minute: "2-digit",
                            })}
                          </span>
                        </div>
                        <div className="flex items-center space-x-1">
                          <User className="h-4 w-4" />
                          <span>สัตวแพทย์</span>
                        </div>
                      </div>
                      {appointment.notes && <p className="text-sm text-gray-600 mt-2">{appointment.notes}</p>}
                    </div>
                  </div>
                  <div className="flex items-center space-x-3">
                    <span
                      className={`px-3 py-1 rounded-full text-sm font-medium ${getStatusColor(appointment.status)}`}
                    >
                      {getStatusText(appointment.status)}
                    </span>
                    {appointment.status === "scheduled" && (
                      <div className="flex space-x-2">
                        <button onClick={() => handleUpdateStatus(appointment.id, 'confirmed')} className="text-green-600 hover:text-green-700" title="ยืนยันนัดหมาย">
                          <CheckCircle className="h-5 w-5" />
                        </button>
                        <button onClick={() => handleUpdateStatus(appointment.id, 'cancelled')} className="text-red-600 hover:text-red-700" title="ยกเลิกนัดหมาย">
                          <XCircle className="h-5 w-5" />
                        </button>
                      </div>
                    )}
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Book Appointment Modal */}
      {showBookModal && (
        <BookAppointmentModal onClose={() => setShowBookModal(false)} onAppointmentBooked={handleAppointmentBooked} />
      )}

      {/* Appointment Detail Modal */}
      {showDetailModal && selectedAppointment && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-xl max-w-md w-full">
            <div className="flex items-center justify-between p-6 border-b">
              <h2 className="text-xl font-bold text-gray-900">รายละเอียดนัดหมาย</h2>
              <button onClick={() => setShowDetailModal(false)} className="text-gray-400 hover:text-gray-600">
                <XCircle className="h-6 w-6" />
              </button>
            </div>
            <div className="p-6 space-y-4">
              <p><strong>สัตว์เลี้ยง:</strong> {selectedAppointment.pets.name}</p>
              <p><strong>สัตวแพทย์:</strong> {selectedAppointment.veterinarian.email}</p>
              <p><strong>วันที่และเวลา:</strong> {new Date(selectedAppointment.appointment_date).toLocaleString('th-TH')}</p>
              <p><strong>สถานะ:</strong> {getStatusText(selectedAppointment.status)}</p>
              <p><strong>ประเภท:</strong> {getAppointmentTypeText(selectedAppointment.appointment_type)}</p>
              {selectedAppointment.notes && <p><strong>หมายเหตุ:</strong> {selectedAppointment.notes}</p>}
            </div>
          </div>
        </div>
      )}
    </div>
  )
}

export default Appointments
