import React, { useState, useEffect } from 'react';
import { getUsers, createUser, updateUser, deleteUser } from '../services/userService';

// This component will manage the state and UI for the users page.
const Users = () => {
  // State to hold the list of users
  const [users, setUsers] = useState([]);
  // State to manage form input for adding/editing users
  const [formData, setFormData] = useState({ name: '', email: '' });
  // State to track the user currently being edited
  const [editingId, setEditingId] = useState(null);
  // State for loading indicators
  const [loading, setLoading] = useState(true);
  // State for error messages
  const [error, setError] = useState(null);

  // Fetch users when the component mounts
  useEffect(() => {
    fetchUsers();
  }, []);

  // Function to fetch all users from the API
  const fetchUsers = async () => {
    try {
      setLoading(true);
      const usersData = await getUsers();
      setUsers(usersData);
      setError(null); // Clear any previous errors
    } catch (err) {
      setError(err.message || 'Failed to fetch users.');
      alert(err.message || 'Failed to fetch users.'); // Show alert on error
    } finally {
      setLoading(false);
    }
  };

  // Handler for form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Handler for form submission (add or update user)
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!formData.name || !formData.email) {
      alert('Name and email are required.');
      return;
    }

    try {
      if (editingId) {
        // If editing, call the update user function
        await updateUser(editingId, formData);
      } else {
        // If not editing, call the create user function
        await createUser(formData);
      }
      // Reset form and editing state
      setFormData({ name: '', email: '' });
      setEditingId(null);
      // Refetch users to show the latest data
      fetchUsers();
    } catch (err) {
      setError(err.message || 'Failed to save user.');
      alert(err.message || 'Failed to save user.'); // Show alert on error
    }
  };

  // Handler to set the form up for editing a user
  const handleEdit = (user) => {
    setEditingId(user.id);
    setFormData({ name: user.name, email: user.email });
  };

  // Handler for deleting a user
  const handleDelete = async (id) => {
    // Confirm before deleting
    if (window.confirm('Are you sure you want to delete this user?')) {
      try {
        await deleteUser(id);
        // Refetch users to update the list
        fetchUsers();
      } catch (err) {
        setError(err.message || 'Failed to delete user.');
        alert(err.message || 'Failed to delete user.'); // Show alert on error
      }
    }
  };

  // Handler to cancel editing
  const handleCancelEdit = () => {
    setEditingId(null);
    setFormData({ name: '', email: '' });
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>User Management</h1>

      {/* Form for adding and editing users */}
      <form onSubmit={handleSubmit} style={{ marginBottom: '20px' }}>
        <input
          type="text"
          name="name"
          placeholder="Name"
          value={formData.name}
          onChange={handleInputChange}
          required
          style={{ marginRight: '10px' }}
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleInputChange}
          required
          style={{ marginRight: '10px' }}
        />
        <button type="submit">{editingId ? 'Update User' : 'Add User'}</button>
        {editingId && (
          <button type="button" onClick={handleCancelEdit} style={{ marginLeft: '10px' }}>
            Cancel
          </button>
        )}
      </form>

      {/* Display loading message */}
      {loading && <p>Loading users...</p>}

      {/* Display error message */}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      {/* Display user list */}
      {!loading && !error && (
        <table style={{ width: '100%', borderCollapse: 'collapse' }}>
          <thead>
            <tr style={{ borderBottom: '1px solid #ddd' }}>
              <th style={{ padding: '8px', textAlign: 'left' }}>ID</th>
              <th style={{ padding: '8px', textAlign: 'left' }}>Name</th>
              <th style={{ padding: '8px', textAlign: 'left' }}>Email</th>
              <th style={{ padding: '8px', textAlign: 'left' }}>Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user) => (
              <tr key={user.id} style={{ borderBottom: '1px solid #ddd' }}>
                <td style={{ padding: '8px' }}>{user.id}</td>
                <td style={{ padding: '8px' }}>{user.name}</td>
                <td style={{ padding: '8px' }}>{user.email}</td>
                <td style={{ padding: '8px' }}>
                  <button onClick={() => handleEdit(user)} style={{ marginRight: '5px' }}>
                    Edit
                  </button>
                  <button onClick={() => handleDelete(user.id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default Users;
